                if (xSignifier & 0x8 > 0)
                    resultSignifier = (resultSignifier * 0x100000000000000000000000000000004) >> 128;
                if (xSignifier & 0x4 > 0)
                    resultSignifier = (resultSignifier * 0x100000000000000000000000000000001) >> 128;

